        private void barButtonItem2_ItemClick(object sender, DevExpress.XtraBars.ItemClickEventArgs e)
        {
            int k = 0;
            for (int i = 0; i < bdsnhapdiem.Count; i++)
            {

                masv = gcdiem["colmasv", i].Value.ToString();
                mamh = cmbMM.Text;
                lan = Int32.Parse(spLT.Text);
                diem = float.Parse(gcdiem["coldiem", i].Value.ToString());
                
                Program.capnhat("INSERT INTO DIEM(MASV,MAMH,LAN,DIEM) VALUES('" + masv + "','" + mamh + "'," + lan + "," + diem + ")");
                k = 1;
              
            }
            if (k == 1)
            {
                MessageBox.Show("Cập nhật thành công", "", MessageBoxButtons.OK);
            }
            btnThoat.Enabled = btnxem.Enabled = btnbd.Enabled = panel1.Enabled = cmbkhoa.Enabled = true;
            btnphuchoi.Enabled = gcdiem.Enabled = btnghi.Enabled = false;
        }

